// src/components/Game.js
import React, { useEffect, useState } from 'react';
import VirtualKeyboard from './VirtualKeyboard';
import { useSelector, useDispatch } from 'react-redux';
import { getrandomWord } from '../redux/slices/randomWordSlice';

const Game = () => {
  const [gameWon, setGameWon] = useState(false);
  const [attemptsHistory, setAttemptsHistory] = useState([]);
  const [currentAttempt, setCurrentAttempt] = useState(null); // Track current attempt to replay
  const dispatch = useDispatch();
  const word = useSelector((state) => state.RandomWord.randomWord);
  const [guessedLetters, setGuessedLetters] = useState([]);
  const [attempts, setAttempts] = useState(0);

  useEffect(() => {
    dispatch(getrandomWord());
  }, [dispatch]);

  useEffect(() => {
    if (word && word.length >= 7 && word.split('').every(letter => guessedLetters.includes(letter))) {
      setGameWon(true);
    }
  }, [guessedLetters, word]);

  const handleLetterClick = (letter) => {
    if (!guessedLetters.includes(letter)) {
      const newGuessedLetters = [...guessedLetters, letter];
      const newAttempts = attempts + 1;
      setGuessedLetters(newGuessedLetters);
      setAttempts(newAttempts);

      const currentWordState = word.split('').map((letter) =>
        newGuessedLetters.includes(letter) ? letter : '_'
      ).join('');

      const remainingLetters = word.split('').filter(letter => !newGuessedLetters.includes(letter)).length;

      setAttemptsHistory([...attemptsHistory, { attempt: newAttempts, remainingLetters }]);
    }
  };

  const displayWord = word ? word.split('').map((letter) => (
    guessedLetters.includes(letter) ? letter : '_'
  )).join(' ') : '';

  const handleReplayClick = (attempt) => {
    setCurrentAttempt(attempt);
    // Reset game state to the state of the selected attempt
    const selectedAttempt = attemptsHistory.find(item => item.attempt === attempt);
    if (selectedAttempt) {
      setGuessedLetters([]);
      setAttempts(selectedAttempt.attempt);
      setAttemptsHistory(attemptsHistory.slice(0, attempt - 1));
      setGameWon(false); // Reset game won state
    }
  };

  return (
    <div>
      {word && word.length >= 7 ? (
        <>
          <h1>Guess the word</h1>
          <p>{displayWord}</p>
          <VirtualKeyboard onLetterClick={handleLetterClick} disabledLetters={guessedLetters} />
          <p>Attempts: {attempts}</p>
          {gameWon ? (
            <p>Congratulations! You have guessed the word in {attempts} attempts.</p>
          ) : (
            <>
              <p>Click on any attempt number below to replay:</p>
              <div>
                {attemptsHistory.map(({ attempt }) => (
                  <button key={attempt} onClick={() => handleReplayClick(attempt)}>
                    Attempt {attempt}
                  </button>
                ))}
              </div>
            </>
          )}

          <table>
            <thead>
              <tr>
                <th>Attempt</th>
                <th>Remaining Letters</th>
              </tr>
            </thead>
            <tbody>
              {attemptsHistory.map(({ attempt, remainingLetters }, index) => (
                <tr key={index}>
                  <td>{attempt}</td>
                  <td>{remainingLetters}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Game;
